// Make iScroll slider work
var myScroll=[];$(document).delegate('[data-role="page"]',"pageshow",function(){var e=$(this);$(e.find(".slider")).each(function(e){myScroll=new iScroll("presentationslider",{snap:!0,momentum:!1,hScrollbar:!1,onScrollEnd:function(){document.querySelector("#indicator > li.active").className="";document.querySelector("#indicator > li:nth-child("+(this.currPageX+1)+")").className="active"}});myScroll.push(new iScroll("categoryslider",{hScroll:!1,vScroll:!0,hScrollbar:!1,vScrollbar:!0,fixedScrollbar:!0,fadeScrollbar:!1,hideScrollbar:!1,bounce:!0,momentum:!0,lockDirection:!0,scrollbarClass:"myScrollbar"}))})});$(document).delegate('[data-role="page"]',"pagehide",function(){for(x in myScroll){myScroll[x].destroy();myScroll[x]=null;myScroll.splice(x,1)}});