//setup myScroll var to handle scrolling with iscroll
myScroll=[];$(document).delegate('[data-role="page"]',"pageshow",function(){if($.mobile.activePage.find("#wrapper-vert").length>0)if(this.id in myScroll)setTimeout(function(){myScroll.area.refresh()},100);else{myScroll[this.id]=new iScroll($.mobile.activePage.find("#wrapper-vert")[0].id,{hScroll:!1,vScroll:!0,hScrollbar:!1,vScrollbar:!0,fixedScrollbar:!0,fadeScrollbar:!1,hideScrollbar:!1,bounce:!0,momentum:!0,lockDirection:!0,scrollbarClass:"myScrollbar"});setTimeout(function(){myScroll.area.refresh()},100)}$.mobile.activePage.find("#wrapper-horz").length>0&&(this.id in myScroll?myScroll[this.id].refresh():myScroll[this.id]=new iScroll($.mobile.activePage.find("#wrapper-horz")[0].id,{hScroll:!0,vScroll:!1,hScrollbar:!0,vScrollbar:!1,fixedScrollbar:!0,fadeScrollbar:!1,hideScrollbar:!1,bounce:!0,momentum:!0,lockDirection:!0,scrollbarClass:"myScrollbar"}))});